// generowanie ID www
 function random_string($length) {
    $key_adam = '';
    $keys = array_merge(range(0, 9), range('a', 'z'));
    for ($i = 0; $i < $length; $i++) {
        $key_adam .= $keys[array_rand($keys)];
    }
    return $key_adam;
}

